const FadeUp = ({ children, delay = 0, className = '' }) => {
  const [v, setV] = useState(false);
  useEffect(() => { const t = setTimeout(() => setV(true), delay); return () => clearTimeout(t); }, [delay]);
  return <div className={className} style={{ opacity: v ? 1 : 0, transform: v ? 'translateY(0)' : 'translateY(16px)', transition: 'all 0.5s cubic-bezier(0.16, 1, 0.3, 1)', transitionDelay: `${delay}ms` }}>{children}</div>;
};

const Badge = ({ children, color = C.accent, bg }) => (
  <span style={{ background: bg || `${color}20`, color, fontSize: 11, padding: '2px 10px', borderRadius: 20, fontWeight: 600, letterSpacing: 0.3 }}>{children}</span>
);

const MetricCard = ({ label, value, sub, icon: Icon, color = C.accent, delay = 0 }) => (
  <FadeUp delay={delay}>
    <div style={{ background: C.card, borderRadius: 14, padding: 20, border: `1px solid ${C.border}`, flex: 1, minWidth: 140 }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
        {Icon && <Icon size={16} style={{ color }} />}
        <span style={{ color: C.textMuted, fontSize: 12, textTransform: 'uppercase', letterSpacing: 1 }}>{label}</span>
      </div>
      <div style={{ fontSize: 28, fontWeight: 700, color }}>{value}</div>
      {sub && <div style={{ fontSize: 12, color: C.textDim, marginTop: 4 }}>{sub}</div>}
    </div>
  </FadeUp>
);

const SectionTitle = ({ children, sub }) => (
  <div style={{ marginBottom: 20 }}>
    <h3 style={{ fontSize: 18, fontWeight: 700, color: C.text, marginBottom: sub ? 4 : 0 }}>{children}</h3>
    {sub && <p style={{ fontSize: 13, color: C.textDim }}>{sub}</p>}
  </div>
);

const Card = ({ children, style = {} }) => (
  <div style={{ background: C.card, borderRadius: 14, padding: 20, border: `1px solid ${C.border}`, ...style }}>{children}</div>
);

