// Reusable book cover thumbnail — uses real cropped photos when available, falls back to CSS
const BookCover = ({ title, index = 0, width = 38, height = 52, fontSize = 7, coverKey }) => {
  if (coverKey && BOOK_COVERS[coverKey]) {
    return (
      <div style={{
        width, height, borderRadius: 4, overflow: 'hidden', flexShrink: 0, position: 'relative',
        boxShadow: '1px 1px 3px rgba(0,0,0,0.15)'
      }}>
        <img src={BOOK_COVERS[coverKey]} alt={title} style={{ width: '100%', height: '100%', objectFit: 'cover', display: 'block' }} />
        <div style={{ position: 'absolute', top: 0, left: 0, bottom: 0, width: 3, background: 'rgba(0,0,0,0.12)' }} />
      </div>
    );
  }
  const cover = bookCovers[index % bookCovers.length];
  return (
    <div style={{
      width, height, borderRadius: 4, background: cover.bg,
      display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-end',
      padding: '3px 2px', flexShrink: 0, position: 'relative', overflow: 'hidden',
      boxShadow: '1px 1px 3px rgba(0,0,0,0.15)'
    }}>
      <div style={{ position: 'absolute', top: 0, left: 0, right: 0, height: '35%', background: `${cover.accent}30` }} />
      <div style={{ position: 'absolute', top: 0, left: 0, bottom: 0, width: 3, background: 'rgba(0,0,0,0.15)' }} />
      <span style={{
        fontSize, fontWeight: 700, color: cover.text, textAlign: 'center',
        lineHeight: 1.1, maxWidth: '90%', overflow: 'hidden',
        textShadow: '0 1px 2px rgba(0,0,0,0.3)', zIndex: 1
      }}>{title}</span>
    </div>
  );
};

